import chai from "chai";
import chatHttp from "chai-http";
import "chai/register-should";
import app from "../index";

chai.use(chatHttp);
const { expect } = chai;

describe("Testing the book endpoints:", () => {
  it("It should create a book", (done) => {
    const book = {
      title: "First Awesome book",
      price: "$9.99",
      description: "This is the awesome book",
    };

    chai
      .request(app)
      .post("/api/v1/books")
      .set("Accept", "application/json")
      .send(book)
      .end((err, res) => {
        expect(res.status).to.equal(201);
        expect(res.body.data).to.include({
          id: 1,
          title: book.title,
          price: book.price,
          description: book.description,
        });
        done();
      });
  });

  it("It should not create a book with incomplete parameters", (done) => {
    const book = {
      price: "$9.99",
      description: "This is the awesome book",
    };
    chai
      .request(app)
      .post("/api/v1/books")
      .set("Accept", "application/json")
      .send(book)
      .end((err, res) => {
        expect(res.status).to.equal(400);
        done();
      });
  });

  it("It should get all books", (done) => {
    chai
      .request(app)
      .get("/api/v1/books")
      .set("Accept", "application/json")
      .end((err, res) => {
        expect(res.status).to.equal(200);
        res.body.data[0].should.have.property("id");
        res.body.data[0].should.have.property("title");
        res.body.data[0].should.have.property("price");
        res.body.data[0].should.have.property("description");
        done();
      });
  });

  it("It should get a particular book", (done) => {
    const bookId = 1;
    chai
      .request(app)
      .get(`/api/v1/books/${bookId}`)
      .set("Accept", "application/json")
      .end((err, res) => {
        expect(res.status).to.equal(200);
        res.body.data.should.have.property("id");
        res.body.data.should.have.property("title");
        res.body.data.should.have.property("price");
        res.body.data.should.have.property("description");
        done();
      });
  });

  it("It should not get a particular book with invalid id", (done) => {
    const bookId = 8888;
    chai
      .request(app)
      .get(`/api/v1/books/${bookId}`)
      .set("Accept", "application/json")
      .end((err, res) => {
        expect(res.status).to.equal(404);
        res.body.should.have
          .property("message")
          .eql(`Cannot find book with the id ${bookId}`);
        done();
      });
  });

  it("It should not get a particular book with non-numeric id", (done) => {
    const bookId = "aaa";
    chai
      .request(app)
      .get(`/api/v1/books/${bookId}`)
      .set("Accept", "application/json")
      .end((err, res) => {
        expect(res.status).to.equal(400);
        res.body.should.have
          .property("message")
          .eql("Please input a valid numeric value");
        done();
      });
  });

  it("It should update a book", (done) => {
    const bookId = 1;
    const updatedBook = {
      id: bookId,
      title: "Updated Awesome book",
      price: "$10.99",
      description: "We have updated the price",
    };
    chai
      .request(app)
      .put(`/api/v1/books/${bookId}`)
      .set("Accept", "application/json")
      .send(updatedBook)
      .end((err, res) => {
        expect(res.status).to.equal(200);
        expect(res.body.data.id).equal(updatedBook.id);
        expect(res.body.data.title).equal(updatedBook.title);
        expect(res.body.data.price).equal(updatedBook.price);
        expect(res.body.data.description).equal(updatedBook.description);
        done();
      });
  });

  it("It should not update a book with invalid id", (done) => {
    const bookId = "9999";
    const updatedBook = {
      id: bookId,
      title: "Updated Awesome book again",
      price: "$11.99",
      description: "We have updated the price",
    };
    chai
      .request(app)
      .put(`/api/v1/books/${bookId}`)
      .set("Accept", "application/json")
      .send(updatedBook)
      .end((err, res) => {
        expect(res.status).to.equal(404);
        res.body.should.have
          .property("message")
          .eql(`Cannot find book with the id: ${bookId}`);
        done();
      });
  });

  it("It should not update a book with non-numeric id value", (done) => {
    const bookId = "ggg";
    const updatedBook = {
      id: bookId,
      title: "Updated Awesome book again",
      price: "$11.99",
      description: "We have updated the price",
    };
    chai
      .request(app)
      .put(`/api/v1/books/${bookId}`)
      .set("Accept", "application/json")
      .send(updatedBook)
      .end((err, res) => {
        expect(res.status).to.equal(400);
        res.body.should.have
          .property("message")
          .eql("Please input a valid numeric value");
        done();
      });
  });

  it("It should delete a book", (done) => {
    const bookId = 1;
    chai
      .request(app)
      .delete(`/api/v1/books/${bookId}`)
      .set("Accept", "application/json")
      .end((err, res) => {
        expect(res.status).to.equal(200);
        expect(res.body.data).to.include({});
        done();
      });
  });

  it("It should not delete a book with invalid id", (done) => {
    const bookId = 777;
    chai
      .request(app)
      .delete(`/api/v1/books/${bookId}`)
      .set("Accept", "application/json")
      .end((err, res) => {
        expect(res.status).to.equal(404);
        res.body.should.have
          .property("message")
          .eql(`Book with the id ${bookId} cannot be found`);
        done();
      });
  });

  it("It should not delete a book with non-numeric id", (done) => {
    const bookId = "bbb";
    chai
      .request(app)
      .delete(`/api/v1/books/${bookId}`)
      .set("Accept", "application/json")
      .end((err, res) => {
        expect(res.status).to.equal(400);
        res.body.should.have
          .property("message")
          .eql("Please provide a numeric value");
        done();
      });
  });
});

import config from "dotenv";
import express from "express";
import BookRoutes from "./server/routes/BookRoutes";

config.config();

const app = express();
app.use(express.json());

const port = process.env.PORT || 8000;

// books routes
app.use("/api/v1/books", BookRoutes);

// when a random route is inputed
app.get("*", (req, res) =>
  res.status(200).send({
    message: "Welcome to this api!",
  })
);

app.listen(port, () => console.log(`App is listening to port: ${port}`));

export default app;

import { Router } from "express";
import BookController from "../controllers/BookController";

const router = Router();

router.get("/", BookController.getAllBooks);
router.post("/", BookController.addBook);
router.get("/:id", BookController.getABook);
router.put("/:id", BookController.updatedBook);
router.delete("/:id", BookController.deleteBook);

export default router;

import BookService from "../services/BookService";
import Util from "../utils/Utils";

const util = new Util();

class BookController {
  static async getAllBooks(req, res) {
    try {
      const allBooks = await BookService.getAllBooks();
      if (allBooks.length > 0) {
        util.setSuccess(200, "Books retrieved", allBooks);
      } else {
        util.setSuccess(200, "No Book Found");
      }
      return util.send(res);
    } catch (e) {
      util.setError(400, e);
      return util.send(res);
    }
  }

  static async addBook(req, res) {
    if (!req.body.title || !req.body.price || !req.body.description) {
      util.setError(400, "Please provide complete details");
      return util.send(res);
    }
    const newBook = req.body;
    try {
      const createdBook = await BookService.addBook(newBook);
      util.setSuccess(201, "Book Added!", createdBook);
      return util.send(res);
    } catch (error) {
      util.setError(400, error.message);
      return util.send(res);
    }
  }

  static async updatedBook(req, res) {
    const alteredBook = req.body;
    const { id } = req.params;
    if (!Number(id)) {
      util.setError(400, "Please input a valid numeric value");
      return util.send(res);
    }
    try {
      const updateBook = await BookService.updateBook(id, alteredBook);
      if (!updateBook) {
        util.setError(404, `Cannot find book with the id: ${id}`);
      } else {
        util.setSuccess(200, "Book updated", updateBook);
      }
      return util.send(res);
    } catch (error) {
      util.setError(404, error);
      return util.send(res);
    }
  }

  static async getABook(req, res) {
    const { id } = req.params;

    if (!Number(id)) {
      util.setError(400, "Please input a valid numeric value");
      return util.send(res);
    }

    try {
      const theBook = await BookService.getABook(id);

      if (!theBook) {
        util.setError(404, `Cannot find book with the id ${id}`);
      } else {
        util.setSuccess(200, "Found Book", theBook);
      }
      return util.send(res);
    } catch (error) {
      util.setError(404, error);
      return util.send(res);
    }
  }

  static async deleteBook(req, res) {
    const { id } = req.params;

    if (!Number(id)) {
      util.setError(400, "Please provide a numeric value");
      return util.send(res);
    }

    try {
      const bookToDelete = await BookService.deleteBook(id);

      if (bookToDelete) {
        util.setSuccess(200, "Book deleted");
      } else {
        util.setError(404, `Book with the id ${id} cannot be found`);
      }
      return util.send(res);
    } catch (error) {
      util.setError(400, error);
      return util.send(res);
    }
  }
}

export default BookController;

import db from "../src/models";

class BookService {
  static async getAllBooks() {
    try {
      return await db.Book.findAll();
    } catch (e) {
      throw e;
    }
  }

  static async addBook(newBook) {
    try {
      return await db.Book.create(newBook);
    } catch (e) {
      throw e;
    }
  }

  static async updateBook(id, updateBook) {
    try {
      const bookToUpdate = await db.Book.findOne({
        where: { id: Number(id) },
      });
      if (bookToUpdate) {
        await db.Book.update(updateBook, { where: { id: Number(id) } });
        return updateBook;
      }
      return null;
    } catch (e) {
      throw e;
    }
  }

  static async getABook(id) {
    try {
      return await db.Book.findOne({
        where: { id: Number(id) },
      });
    } catch (e) {
      throw e;
    }
  }
  static async deleteBook(id) {
    try {
      const bookToDelete = await db.Book.findOne({
        where: { id: Number(id) },
      });
      if (bookToDelete) {
        return await db.Book.destroy({
          where: { id: Number(id) },
        });
      }
      return null;
    } catch (e) {
      throw e;
    }
  }
}

export default BookService;

import fs from "fs";
import path from "path";
import { Sequelize } from "sequelize";
import configJson from "../config/config.js";
const basename = path.basename(__filename);

console.log(process.env.NODE_ENV);
const env = process.env.NODE_ENV ? process.env.NODE_ENV : "development";

console.log(configJson[env]);

const config = configJson[env];
console.log("this is the environment: ", env);

const db = {};

let sequelize;
if (config.environment === "production") {
  sequelize = new Sequelize(process.env[config.use_env_variable], config);
  sequelize = new Sequelize(
    process.env.DB_NAME,
    process.env.DB_USER,
    process.env.DB_PASS,
    {
      host: process.env.DB_HOST,
      port: process.env.DB_PORT,
      dialect: "postgres",
      dialectOption: {
        ssl: true,
        native: true,
      },
      logging: true,
    }
  );
} else {
  sequelize = new Sequelize(
    config.database,
    config.username,
    config.password,
    config
  );
}

fs.readdirSync(__dirname)
  .filter((file) => {
    return (
      file.indexOf(".") !== 0 && file !== basename && file.slice(-3) === ".js"
    );
  })
  .forEach((file) => {
    const model = sequelize.import(path.join(__dirname, file));
    db[model.name] = model;
  });

Object.keys(db).forEach((modelName) => {
  if (db[modelName].associate) {
    db[modelName].associate(db);
  }
});

db.sequelize = sequelize;
db.Sequelize = Sequelize;

export default db;

